var currentPage = 1;
var nStart = 0;
var nDisplay = 25;
var workingList = []
var filteredStoresList = [];
var sortOrder = 1;
var prevSort = "Name";


// Drawing Page
// ---------------------------------------------------- //


function displayTable() {
    $("#storesTable").html("");

    // draw N = nDisplay rows in the table
    for (var i = nStart; i < (nStart + nDisplay); i++) {
        var store = workingList[i];
        var rowText = "<tr><td><a href=\"" + store.URL + "\">" + store.Name +
            "</a></td><td>" + store.Keywords + "</td><td>" + store.Price +
            "</td><td>" + store.Country + "</td><td>" + store.Notes + "</td></tr>";
        $("#storesTable").append(rowText);
    }
}

function displayPagination() {
    var numPages = Math.ceil(workingList.length / nDisplay);
    currentPage = Math.floor(nStart / nDisplay) + 1;

    // clear, then draw previous button
    $("#pageSwitch").html("");
    $("#pageSwitch").append("<li><a class=\"arrowBtn\" onClick=\"goToPage(" + 1 + ")\"><<</a></li>");
    $("#pageSwitch").append("<li><a class=\"arrowBtn\" onClick=\"prevPage()\"><</a></li>");

    // display numbers in the middle
    // if i == currentPage, disable click on that number
    var startPage = currentPage > 5 ? currentPage - 5 : 1;
    var endPage = currentPage > numPages - 5  ? numPages : currentPage + 5;
    if (startPage > 1)
        $("#pageSwitch").append("<li>...</li>");
    for (var i = startPage; i <= endPage; i++) {
        if (i == currentPage)
            $("#pageSwitch").append("<li><strong>" + i + "</strong></li>");
        else
            $("#pageSwitch").append("<li><a class=\"numLink\" onClick=\"goToPage(" + i + ")\">" + i + "</a></li>");
    }
    if (endPage < numPages)
        $("#pageSwitch").append("<li>...</li>");

    // draw next button
    $("#pageSwitch").append("<li><a class=\"arrowBtn\" onClick=\"nextPage()\">></a></li>");
    $("#pageSwitch").append("<li><a class=\"arrowBtn\" onClick=\"goToPage(" + numPages + ")\">>></a></li>");
}

function redrawPage() {
    displayPagination();
    displayTable();
}


// Page Navigation
// ---------------------------------------------------- //


function prevPage() {
    // if not already at the first page, move to previous page
    if (nStart == 0)
        return;
    nStart -= nDisplay;
    changeSort();
    redrawPage();
}

function nextPage() {
    // if not already at the last page, move to previous page
    var newStart = nStart + nDisplay;
    if (newStart > workingList.length - 1)
        return;
    nStart += nDisplay;
    changeSort();
    redrawPage();
}

function goToPage(n) {
    // Go to page n
    if (n == "")
        return;

    // math.round(number(n)) handles inputs with decimals
    n = Math.round(Number(n));
    numPages = Math.ceil(workingList.length / nDisplay);

    if (n < 1)
        n = 1;
    else if (n > numPages)
        n = numPages;

    nStart = (n - 1) * nDisplay;
    changeSort();
    redrawPage();
}

function displayNStores() {
    // display n stores per page
    var selectedVal = $("#displayNumber").val();
    if (selectedVal == "all")
        nDisplay = workingList.length;
    else
        nDisplay = Number(selectedVal);
    nStart = 0;
    changeSort();
    redrawPage();
}


// Search
// ---------------------------------------------------- //


function clearSearch() {
    $("#searchInput").val("");
    workingList = storesList;
    $("#clearSearchBtn").hide();
    nStart = 0;
    changeSort();
    redrawPage();
}

// TODO: Fix so that searching for "$" gives only stores with cost "$", etc
function performSearch() {
    var query = $("#searchInput").val();
    var searchCategory = $("#searchCategory").val();
    filteredStoresList = [];

    if (query == "") {
        clearSearch();
        return;
    }

    for (var i = 0; i < storesList.length; i++) {
        var store = storesList[i];
        if (store[searchCategory].toLowerCase().includes(query.toLowerCase()))
            filteredStoresList.push(store);
    }

    workingList = filteredStoresList;
    nStart = 0;
    $("#clearSearchBtn").show();
    changeSort();
    redrawPage();
}


// Sort
// ---------------------------------------------------- //

function sortBy(category) {
    // start of new text to put into the <th> tag
    var newThText = category;

    // switch sort order or sort by new category depending on what was
    // previously sorted
    if (prevSort == category) {
        sortOrder *= -1;
        if (sortOrder == 1)
            newThText += " ▲";
        else
            newThText += " ▼";
    }
    else {
        // reset text of previous sort header
        $("th[category='" + prevSort + "']").text(prevSort);
        newThText += " ▲";
        sortOrder = 1;
    }

    // change text of new sort header
    $("th[category='" + category + "']").text(newThText);
    prevSort = category;
    changeSort();
    redrawPage();
}

function changeSort() {
    workingList.sort(function(a,b) {
        var a2 = a[prevSort].toLowerCase();
        var b2 = b[prevSort].toLowerCase();
        if (a2 > b2) return (1 * sortOrder);
        else if (a2 < b2) return (-1 * sortOrder);
        return 0;
    });
}


// Perform once on load
// ---------------------------------------------------- //


$(document).ready(function() {
    // hide button, set working list
    $("#clearSearchBtn").hide();
    workingList = storesList;

    // draw page
    displayTable();
    displayPagination();

    // bind actions to events
    $("th").on("click", function(e) {
        //console.log(e);
        sortBy(String(e.target.attributes["category"].value));
    });
    $("#helpBtn").on("click", function() {
        if ($("#helpArea").is(":visible")) $("#helpArea").hide(100);
        else { $("#helpArea").show(100); }
    });
    $("#displayNumber").on("change", displayNStores);
    $("#pageInput").on("keypress", function(e) {
        if (e.keyCode == 13) {
            var n = $("#pageInput").val();
            $("#pageInput").val("");
            goToPage(n);
        }
    });
    $("#searchInput").on("keypress", function(e) {
        if (e.keyCode == 13)
            performSearch();
    });
});


